/**
 * Representation for iterating over a snapshot's Nodes and Edges.
 * The structure of a snapshot's Node and Edge is defined within the contents of the snapshot.
 */
export default class HeapSnapshot {
  /**
   * @param {SplitSnapshotProvider} provider Snapshot data provider
   */
  constructor(provider) {
    /**
     * @private
     */
    this.meta = provider.getMeta();
    /**
     * @private
     */
    this.Node = createNodeClass(this, provider);
    /**
     * @private
     */
    this.Edge = createEdgeClass(this, provider);
    /**
     * @private
     */
    this.provider = provider;
  }

  /**
  * @typedef {Object} NodeResult
  * @property {Node} fields
  * @property {number} node_index Index that can be used with getNode to get this Node.
  * @property {number} edge_index Index that can be used to get the first Edge of this Node.
  * @property {function():EdgeIterator} walkEdges Helper for iterating the edges of a Node.
  */
  /**
  * Gets a Node by index, not by ID. The root Node is at index 0.
  * @param {number} node_index
  * @return {NodeResult}
  */
  getNode(node_index) {
    if (node_index > this.provider.getNodeArraySize()) {
      return null;
    }
    const node_buffer = this.provider.getNodeBuffer(node_index);
    const node = new this.Node(node_buffer);
    const edge_index = node_buffer.readUInt32BE(node_buffer.length - 4);
    const self = this;
    return {
      fields: node,
      node_index,
      edge_index,
      *walkEdges() {
        let edge_count = node.edge_count;
        let edge_size = self.meta.edge_fields.length;
        for (let i = 0; i < edge_count; i++) {
          let index = (edge_index + i) * edge_size;
          let e = self.getEdge(index);
          if (e != null) {
            yield e;
          }
        }
      }
    };
  }

  /**
  * @typedef {Object} EdgeResult
  * @property {Edge} fields
  * @property {number} index Index that can be used with getEdge to get this Edge.
  * @property {function():NodeResult} getNode Helper for getting the Node this Edge points to.
  */
  /**
  * Gets an Edge by index. This should only be used in conjuction with a Node object.
  * @param {number} edge_index Index of the Edge we wish to get a hold of.
  * @return {EdgeResult}
  */
  getEdge(edge_index) {
    if (edge_index > this.provider.getEdgeArraySize()) {
      return null;
    }
    const edge_buffer = this.provider.getEdgeBuffer(edge_index);
    const fields = new this.Edge(edge_buffer);
    const self = this;
    return {
      fields,
      edge_index,
      getNode() {
        return self.getNode(fields.to_node);
      }
    };
  }
}
/**
 * Generates an Node class tailored for HeapSnapshot. These can vary between snapshots.
 * @param {HeapSnapshot} snapshot HeapSnapshot that created this Node class
 * @param {SplitSnapshotProvider} provider Our snapshot data
 */
function createNodeClass(snapshot, provider) {
  const meta = provider.getMeta();
  class Node {
    constructor(buffer) {
      this._buffer = buffer;
    }
    inspect() {
      let ret = '';
      let i = 0;
      for (const field of meta.node_fields) {
        ret += ' '+[field, this[field], this._buffer.slice(i * 4, i * 4 + 4).toString('hex')].join(':');
        i++;
      }
      return `HeapSnapshot::Node${ret}`;
    }
  }
  let i = 0;
  for (const field of meta.node_fields) {
    let field_index = i * 4;
    let field_type = meta.node_types[i];
    if (Array.isArray(field_type)) {
      Object.defineProperty(Node.prototype, field, {
        enumerable: true,
        get() {
          return field_type[this._buffer.readUInt32BE(field_index)];
        }
      });
    }
    else if (field_type === 'string') {
      Object.defineProperty(Node.prototype, field, {
        enumerable: true,
        get() {
          return provider.getString(this._buffer.readUInt32BE(field_index));
        }
      });
    }
    else {
      Object.defineProperty(Node.prototype, field, {
        enumerable: true,
        get() {
          return this._buffer.readUInt32BE(field_index);
        }
      });
    }
    i++;
  };
  return Node;
}
/**
 * Generates an Edge class tailored for HeapSnapshot. These can vary between snapshots.
 * @param {HeapSnapshot} snapshot HeapSnapshot that created this Edge class
 * @param {SplitSnapshotProvider} provider Our snapshot data
 */
function createEdgeClass(snapshot, provider) {
  const meta = provider.getMeta();
  class Edge {
    constructor(buffer) {
      this._buffer = buffer;
    }
    inspect() {
      let ret = '';
      let i = 0;
      for (const field of meta.edge_fields) {
        ret += ' '+[field, this[field], this._buffer.slice(i * 4, i * 4 + 4).toString('hex')].join(':');
        i++;
      }
      ret += ' node.id:' + snapshot.getNode(this.to_node).node.id;
      return `HeapSnapshot::Edge ${ret}`;
    }
  }
  let i = 0;
  for (let field of meta.edge_fields) {
    (function(){ 
    const field_index = i * 4;
    const field_type = meta.edge_types[i];
    i++;
    if (Array.isArray(field_type)) {
      Object.defineProperty(Edge.prototype, field, {
        enumerable: true,
        get() {
          return field_type[this._buffer.readUInt32BE(field_index)];
        }
      });
    }
    else if (field === 'name_or_index') {
      Object.defineProperty(Edge.prototype, field, {
        enumerable: true,
        get() {
          let value = this._buffer.readUInt32BE(field_index);
          if (this.type !== 'element') {
            value = provider.getString(value).toString();
          }
          return value;
        }
      });
    }
    else {
      Object.defineProperty(Edge.prototype, field, {
        enumerable: true,
        get() {
          return this._buffer.readUInt32BE(field_index);
        }
      });
    }
    })();
  };
  return Edge;
}

import fs from "fs";
import path from "path";
import parseSnapshotStream from "./parseSnapshotStream";

/**
 * An implementation of working directly with snapshot data, not meant for dev consumption.
 * This implementation uses Synchronous file APIs
 */
export default class SplitSnapshotProvider {
	/**
	 * @param {Object} snapshot_info Meta information required for understanding a snapshot
	 * @param {Buffer} node_buffer
	 * @param {Buffer} edge_buffer
	 * @param {Buffer} string_indices_buffer
	 * @param {Buffer} string_buffer
	 */
  constructor(
    snapshot_info,
    node_buffer,
    edge_buffer,
    string_indices_buffer,
    string_buffer
  ) {
    const snapshot = snapshot_info;
    /**
     * @private
     */
    this.snapshot = snapshot;

    /**
     * @private
     */
    this.node_fields_length = snapshot.meta.node_fields.length;
    /**
     * @private
     */
	this.node_arr_length = snapshot.node_count * this.node_fields_length;
    // append edge_buffer index to get 1st edge
    /**
     * @private
     */
	this.node_struct_size = this.node_fields_length * 4 + 4;
    /**
     * @private
     */
    this.node_buffer = node_buffer;
	if (this.node_buffer.length !== this.node_struct_size * snapshot.node_count) {
		throw new Error('nodes buffer does not match the expected size (either number of nodes is incorrect, or structure is)');
	}

    /**
     * @private
     */
	this.edge_fields_length = snapshot.meta.edge_fields.length;
    /**
     * @private
     */
	this.edge_arr_length = snapshot.edge_count * this.edge_fields_length;
    // prepend node_buffer index to get node
    /**
     * @private
     */
	this.edge_struct_size = snapshot.meta.edge_fields.length * 4;
    /**
     * @private
     */
    this.edge_buffer = edge_buffer;
	if (this.edge_buffer.length !== this.edge_struct_size * snapshot.edge_count) {
		throw new Error('edges buffer does not match the expected size (either number of edges is incorrect, or structure is)')
	}

    /**
     * @private
     */
    this.string_indices_buffer = string_indices_buffer;
    /**
     * @private
     */
    this.strings_buffer = string_buffer;

    /**
     * @private
     */
	this.string_index_struct_size = 4 + 4;
  }


  /**
   * Get total number of Nodes
   * @return {number}
   */
  getNodeArraySize() {
    return this.node_arr_length;
  }


  /**
   * Get the buffer for a Node at the specified index
   * @param {number} index
   * @return {Buffer}
   */
  getNodeBuffer(index) {
    let n = index / this.node_fields_length;
    if (n !== (n | 0)) {
      throw new RangeError('index is not on a Node boundary');
    }
    let offset = n * this.node_struct_size;
    return this.node_buffer.slice(
      offset,
      offset + this.node_struct_size
    );
  }


  /**
   * Get total number of Edges
   * @return {number}
   */
  getEdgeArraySize() {
    return this.edge_arr_length;
  }


  /**
   * Get the buffer for an Edge at the specified index
   * @param {number} index
   * @return {Buffer}
   */
  getEdgeBuffer(index) {
    let n = index / this.edge_fields_length;
    if (n !== (n | 0)) {
      throw new RangeError('index is not on a Edge boundary');
    }
    let offset = n * this.edge_struct_size;
    return this.edge_buffer.slice(
      offset,
      offset + this.edge_struct_size
    );
  }


  /**
   * Gets a string at the specified index.
   * @param {number} index Index of the string we wish to get.
   * @return {string} 
   */
  getString(index) {
    const index_offset = index * this.string_index_struct_size;
    const chunk = this.string_indices_buffer.slice(
      index_offset,
      index_offset + this.string_index_struct_size
    );
    const offset = chunk.readUInt32BE(0);
    const length = chunk.readUInt32BE(4);
    return this.strings_buffer.slice(
      offset,
      offset + length
    ).toString();
  }

  /**
	* @return {Object} Metadata from the snapshot data required to find Node and Field structure.
	*/
  getMeta() {
    return this.snapshot.meta;
  }

  /**
   * Convenience method for saving snapshot data in a faster loading format to a directory
   * @param {string} outdir Path of the directory to save to
   * @param {function(err: *)} callback Callback notified when there is an error or completion
   * @return {undefined}
   */
  writeToDirectory(outdir, callback) {
		const meta_file = path.join(outdir, 'snapshot.json');
		const nodes_file = path.join(outdir, 'nodes');
		const edges_file = path.join(outdir, 'edges');
		const strings_file = path.join(outdir, 'strings');
		const string_indices_file = path.join(outdir, 'string_indices');

		let todo = 5;
		function done(e) {
			todo--;
			if (todo < 0) {
				return;
			}
			if (e) {
				callback(e);
				todo = 0;
			}
			else if (todo === 0) {
				callback(null);
			}
		}

		fs.writeFile(meta_file, this.snapshot, done);
		fs.writeFile(nodes_file, this.node_buffer, done);
		fs.writeFile(edges_file, this.edge_buffer, done);
		fs.writeFile(strings_file, this.strings_buffer, done);
		fs.writeFile(string_indices_file, this.string_indices_buffer, done);
	}



  /**
   * Convenience method for loading snapshot data from a directory
   * @param {string} outdir Path of the directory to load
   * @param {function(err: *, provider: SplitSnapshotProvider)} callback Callback notified when there is an error or completion
   * @return {undefined}
   */
	static fromDirectory(outdir, callback) {
		const meta_file = path.join(outdir, 'snapshot.json');
		const nodes_file = path.join(outdir, 'nodes');
		const edges_file = path.join(outdir, 'edges');
		const strings_file = path.join(outdir, 'strings');
		const string_indices_file = path.join(outdir, 'string_indices');

		let todo = 5;
		let snapshot_info;
		let nodes_buffer;
		let edges_buffer;
		let strings_buffer;
		let string_indices_buffer;
		function done(e) {
			todo--;
			if (todo < 0) {
				return;
			}
			if (e) {
				callback(e);
				todo = 0;
			}
			else if (todo === 0) {
				callback(null, new SplitSnapshotProvider(
					snapshot_info,
					nodes_buffer,
					edges_buffer,
					string_indices_buffer,
					strings_buffer
				));
			}
		}
		fs.readFile(meta_file, (e,b) => {
			if (e) {
				done(e);
				return;
			}
			try {
				snapshot_info = JSON.parse(String(b));
				done();
			}
			catch (e) {
				done(e);
			}
		});
		fs.readFile(nodes_file, (e,b) => {
			if (e) {
				done(e);
				return;
			}
			nodes_buffer = b;
			done();
		});
		fs.readFile(edges_file, (e,b) => {
			if (e) {
				done(e);
				return;
			}
			edges_buffer = b;
			done();
		});
		fs.readFile(strings_file, (e,b) => {
			if (e) {
				done(e);
				return;
			}
			strings_buffer = b;
			done();
		});
		fs.readFile(string_indices_file, (e,b) => {
			if (e) {
				done(e);
				return;
			}
			string_indices_buffer = b;
			done();
		});
	}


  /**
   * Convenience method for loading snapshot data from a stream
   * @param {Stream} stream Path of the directory to load
   * @param {function(err: *, provider: SplitSnapshotProvider)} callback Callback notified when there is an error or completion
   * @return {undefined}
   */
	static fromStream(stream, callback) {
		// track this to attach to nodes
		let edge_offset = 0;

		let edge_count_field = 0;

		let snapshot_info = null;
		let nodes_buffer_index = 0;
		let nodes_buffer = null;
		let edges_buffer_index = 0;
		let edges_buffer = null;
		let strings_buffer_length = 0;
		let strings_buffer = null;
		let strings_indices_buffer_length = 0;
		let strings_indices_buffer = null;

		stream.pipe(parseSnapshotStream({
			onsnapshot_info(snapshot) {
				edge_count_field = snapshot.meta.node_fields.indexOf('edge_count') * 4;
				snapshot_info = snapshot;
				const node_fields_length = snapshot.meta.node_fields.length;
				// append edge_buffer index to get 1st edge
				const node_struct_size = node_fields_length * 4 + 4;
				nodes_buffer = new Buffer(node_struct_size * snapshot.node_count);

				// prepend node_buffer index to get node
				const edge_struct_size = snapshot.meta.edge_fields.length * 4;
				edges_buffer = new Buffer(edge_struct_size * snapshot.edge_count);

				strings_indices_buffer = new Buffer(4 * 1024);
				strings_buffer = new Buffer(4 * 1024);
			},
			onnode(buffer) {
				if (buffer == null) {
					return;
				}
				buffer.copy(nodes_buffer, nodes_buffer_index);
				nodes_buffer_index += buffer.length;
				nodes_buffer.writeUInt32BE(edge_offset, nodes_buffer_index);
				nodes_buffer_index += 4;
				const edge_count = buffer.readUInt32BE(edge_count_field);
				edge_offset += edge_count;
			},
			onedge(buffer) {
				if (buffer == null) {
					return;
				}
				buffer.copy(edges_buffer, edges_buffer_index);
				edges_buffer_index += buffer.length;
			},
			onstring(str) {
				if (str == null) {
					strings_indices_buffer = strings_indices_buffer.slice(0, strings_indices_buffer_length);
					strings_buffer = strings_buffer.slice(0, strings_buffer_length);
					return;
				}
				let str_buf = new Buffer(str, 'utf8');
				let str_buf_length = str_buf.length;

				if (strings_indices_buffer.length - strings_indices_buffer_length < 8) {
					strings_indices_buffer = Buffer.concat([strings_indices_buffer, new Buffer(4096)]);
				}
				strings_indices_buffer.writeUInt32BE(strings_buffer_length, strings_indices_buffer_length);
				strings_indices_buffer_length += 4;
				strings_indices_buffer.writeUInt32BE(str_buf_length, strings_indices_buffer_length);
				strings_indices_buffer_length += 4;

				if (strings_buffer.length - strings_buffer_length < str_buf_length) {
					strings_buffer = Buffer.concat([strings_buffer, new Buffer(4096)]);
				}
				str_buf.copy(strings_buffer, strings_buffer_length);
				strings_buffer_length += str_buf_length;
			}
		}))
		.on('error', e=>callback(e))
		.on('end', ()=>{
			callback(null, new SplitSnapshotProvider(snapshot_info, nodes_buffer, edges_buffer, strings_indices_buffer, strings_buffer));
		});
	}
}

import {HeapSnapshot,SplitSnapshotProvider} from "../";

// We are going to use stdin to read our snapshot
// pipe a snapshot in via: `node dump.js <"my.heapsnapshot"`
const stream = process.stdin;

// This is used to parse the snapshot data.
// A provider is generally not used for analyzing the snapshot.
// It is an abstraction to allow saving/loading the snapshot to different
// location.
SplitSnapshotProvider.fromStream(stream, (err, provider) => {
	if (err) {
		console.error(err);
		process.exit(1);
	}
	// This gives us an API that can be used to analyze the snapshot.
	// Since snapshot data contains the structure of Nodes and Edges
	// the Node and Edge classes we obtain from this may be different
	// from different snapshots.
	const snapshot = new HeapSnapshot(provider);

	// Heaps are graphs, they can contain cycles! So we use a Set to
	// keep track of Nodes we have already seen.
	const visited = new Set();

	// We will be keeping a list of all the Edges we need to cross
	// still in an array.
	const edges_to_visit = [];

	// Heaps always have a root Node (index == 0)
	// This is the first Node you should visit when walking a Heap
	{
		const root = snapshot.getNode(0);
		// for simplicity lets make a mock Edge for our root
		// so that our walking loop always takes Edges
		// and push that onto the Edges we should visit
		edges_to_visit.push({
			getNode() {return root;}
		})
	}

	while (edges_to_visit.length) {
		// While walking we will grab the first Edge off our list
		const edge_to_walk = edges_to_visit.shift();

		// We grab the Node that this edge points to
		const node = edge_to_walk.getNode();

		// We want to be sure we don't start a cycle so we skip
		// Nodes we have already visited (but not Edges to those Nodes
		// in this example)
		if (visited.has(node.node_index)) {
			continue;
		}
		visited.add(node.node_index);

		// Print the data!
		console.log(node.fields);

		// Add all of the edges of a node to the list of Edges we
		// need to visit
		for (const edge of node.walkEdges()) {
			edges_to_visit.push(edge);
		}
	}
});

